using System.Net.Mail;\n\npublic void SendEmailFromYahoo()\n{\n    SmtpClient smtpClient = new SmtpClient("smtp.mail.yahoo.com");\n    smtpClient.Port = 587;\n    smtpClient.EnableSsl = true;\n    smtpClient.Credentials = new System.Net.NetworkCredential("your_yahoo_email_id", "your_yahoo_password");\n\n    MailMessage mailMessage = new MailMessage("your_yahoo_email_id", "recipient_email_id", "Subject", "Body");\n    smtpClient.Send(mailMessage);\n}