// assume 'originalDataSet' is your original DataSet with 28 columns\n\n// create a new DataSet to hold the subset\nDataSet subsetDataSet = new DataSet();\n\n// create two DataTables, each with 14 columns\nDataTable table1 = new DataTable();\nDataTable table2 = new DataTable();\n\n// add columns to table1\nfor (int i = 0; i < 14; i++)\n{\n    table1.Columns.Add(originalDataSet.Tables[0].Columns[i].ColumnName, originalDataSet.Tables[0].Columns[i].DataType);\n}\n\n// add columns to table2\nfor (int i = 14; i < 28; i++)\n{\n    table2.Columns.Add(originalDataSet.Tables[0].Columns[i].ColumnName, originalDataSet.Tables[0].Columns[i].DataType);\n}\n\n// add rows to table1 and table2\nforeach (DataRow row in originalDataSet.Tables[0].Rows)\n{\n    DataRow newRow1 = table1.NewRow();\n    DataRow newRow2 = table2.NewRow();\n\n    for (int i = 0; i < 14; i++)\n    {\n        newRow1[i] = row[i];\n    }\n\n    for (int i = 14; i < 28; i++)\n    {\n        newRow2[i - 14] = row[i];\n    }\n\n    table1.Rows.Add(newRow1);\n    table2.Rows.Add(newRow2);\n}\n\n// add tables to subsetDataSet\nsubsetDataSet.Tables.Add(table1);\nsubsetDataSet.Tables.Add(table2);