protected override void CreateChildControls()\n{\n    base.CreateChildControls();\n\n    // Create the panel, textbox, and validators here\n    Panel panel = new Panel();\n    TextBox textBox = new TextBox();\n    textBox.ID = "myTextBox";\n\n    RequiredFieldValidator requiredValidator = new RequiredFieldValidator();\n    requiredValidator.ID = "myRequiredValidator";\n    requiredValidator.ControlToValidate = textBox.ID;\n\n    RegularExpressionValidator regexValidator = new RegularExpressionValidator();\n    regexValidator.ID = "myRegexValidator";\n    regexValidator.ControlToValidate = textBox.ID;\n\n    panel.Controls.Add(textBox);\n    panel.Controls.Add(requiredValidator);\n    panel.Controls.Add(regexValidator);\n\n    // Add the panel to the table cell\n    TableCell cell = new TableCell();\n    cell.Controls.Add(panel);\n\n    // Add the table cell to the table\n    Table table = new Table();\n    table.Rows.Add(new TableRow());\n    table.Rows[0].Cells.Add(cell);\n\n    // Add the table to the page\n    this.Controls.Add(table);\n}