using System.Net;\nusing System.Net.Http;\n\n// Create a cookie container\nvar cookieContainer = new CookieContainer();\n\n// Create an instance of HttpClient with the cookie container\nvar handler = new HttpClientHandler { CookieContainer = cookieContainer };\nvar client = new HttpClient(handler);\n\n// Make requests as needed\nvar response1 = await client.GetAsync("https://example.com/login");\nvar response2 = await client.PostAsync("https://example.com/doSomething", new StringContent("data", Encoding.UTF8, "text/plain"));\n\n// Cookies will be persisted across requests