using RestSharp;\n\nvar client = new RestClient("https://example.com");\nclient.CookieContainer = new CookieContainer();\n\nvar request1 = new RestRequest("/login", Method.GET);\nvar response1 = await client.ExecuteAsync(request1);\n\nvar request2 = new RestRequest("/doSomething", Method.POST);\nrequest2.AddParameter("data", "some data", ParameterType.RequestBody);\nvar response2 = await client.ExecuteAsync(request2);