public static Bitmap GetStyleBitmap(Style style)\n{\n    if (style == null)\n    {\n        throw new ArgumentNullException(nameof(style));\n    }\n\n    using (var bm = new Bitmap(16, 16, System.Drawing.Imaging.PixelFormat.Format32bppArgb))\n    {\n        var rect = new System.Drawing.Rectangle(0, 0, 16, 16);\n        var ss = new StyleSample { Bounds = rect };\n\n        if (style is CompositeStyle compositeStyle)\n        {\n            ss.ApplyAreaStyle(compositeStyle.AreaStyle);\n            ss.ApplyLineStyle(compositeStyle.LineStyle);\n        }\n        else if (style is AreaStyle areaStyle)\n        {\n            ss.ApplyAreaStyle(areaStyle);\n        }\n        else if (style is SimpleLineStyle simpleLineStyle)\n        {\n            ss.ApplyLineStyle(simpleLineStyle);\n        }\n\n        using (var me = new MapExport(ss.Map))\n        {\n            var image = me.Export();\n            return new Bitmap(image);\n        }\n    }\n}