using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        var stopwatch = new Stopwatch();\n        stopwatch.Start();\n\n        while (true)\n        {\n            if (stopwatch.ElapsedMilliseconds % 10 == 0)\n            {\n                // trigger your procedure here\n                Console.WriteLine("Tick!");\n            }\n        }\n    }\n}