protected void Page_Load(object sender, EventArgs e)\n{\n    Guid guid = Guid.Parse(Request.QueryString["guid"]);\n    LinqDataSourceSettings settings = (LinqDataSourceSettings)Session[guid.ToString()];\n\n    myLinqDS.ContextTypeName = settings.ContextTypeName;\n    myLinqDS.TableName = settings.TableName;\n    myLinqDS.OrderBy = settings.OrderBy;\n    myLinqDS.Where = settings.Where;\n    myLinqDS.WhereParameters = settings.WhereParameters;\n\n    aDataList.DataSource = myLinqDS;\n    aDataList.DataBind();\n}\n\npublic class LinqDataSourceSettings\n{\n    public string ContextTypeName { get; set; }\n    public string TableName { get; set; }\n    public string OrderBy { get; set; }\n    public string Where { get; set; }\n    public ParameterCollection WhereParameters { get; set; }\n}