using NetMQ;\n\n// Create a context\nusing (var context = new Context())\n{\n    // Create a publisher socket\n    using (var publisher = context.CreateSocket(EndPointType.Pub))\n    {\n        // Bind the publisher to a port\n        publisher.Bind("tcp://localhost:12345");\n\n        // Create a subscriber socket\n        using (var subscriber = context.CreateSocket(EndPointType.Sub))\n        {\n            // Connect the subscriber to the publisher\n            subscriber.Connect("tcp://localhost:12345");\n\n            // Subscribe to a topic\n            subscriber.Subscribe("mytopic");\n\n            // Send a message from the publisher\n            publisher.Send("mytopic", "Hello, world!");\n\n            // Receive the message from the subscriber\n            var message = subscriber.Receive();\n            Console.WriteLine(message);\n        }\n    }\n}