using System;\nusing System.Net;\nusing System.Diagnostics;\n\npublic class WebPageLoader\n{\n    public static void MeasureLoadTime(string url)\n    {\n        var stopwatch = new Stopwatch();\n        stopwatch.Start();\n\n        var request = (HttpWebRequest)WebRequest.Create(url);\n        request.Method = "GET";\n\n        using (var response = (HttpWebResponse)request.GetResponse())\n        {\n            stopwatch.Stop();\n            Console.WriteLine($"Response Time: {stopwatch.ElapsedMilliseconds}ms");\n\n            // Measure the time it takes to fully load the page\n            var html = new HtmlDocument();\n            html.LoadHtml(response.GetResponseStream());\n\n            var imgStopwatch = new Stopwatch();\n            imgStopwatch.Start();\n\n            // Iterate through all images and measure the time it takes to load them\n            foreach (var img in html.DocumentNode.Descendants("img"))\n            {\n                var imgUrl = img.GetAttributeValue("src", string.Empty);\n                if (!string.IsNullOrEmpty(imgUrl))\n                {\n                    using (var imgRequest = (HttpWebRequest)WebRequest.Create(imgUrl))\n                    {\n                        imgRequest.Method = "GET";\n                        using (var imgResponse = (HttpWebResponse)imgRequest.GetResponse())\n                        {\n                            // Do nothing, just wait for the image to load\n                        }\n                    }\n                }\n            }\n\n            imgStopwatch.Stop();\n            Console.WriteLine($"Page Load Time (including images): {imgStopwatch.ElapsedMilliseconds}ms");\n        }\n    }\n}