using System;\nusing System.Web;\n\npublic class Compression\n{\n    public static void EnableCompression(HttpApplication app)\n    {\n        // Get the current HttpApplication instance\n        HttpApplicationState appState = app.Application;\n\n        // Enable compression for static and dynamic files\n        appState["EnableStaticCompression"] = true;\n        appState["EnableDynamicCompression"] = true;\n    }\n}