using iTextSharp.text.pdf;\n\npublic void ModifyPdfProperties(string inputFile, string outputFile)\n{\n    using (var pdfReader = new PdfReader(inputFile))\n    {\n        using (var pdfStamper = new PdfStamper(pdfReader, new FileStream(outputFile, FileMode.Create)))\n        {\n            var pdfInfo = pdfStamper.Writer.Info;\n            pdfInfo.Title = "Your Title";\n            pdfInfo.Author = "Your Author";\n            pdfInfo.Subject = "Your Subject";\n        }\n    }\n}