using Amazon.S3;\nusing Amazon.S3.Model;\n\n// Initialize S3 client\nvar s3Client = new AmazonS3Client("YOUR_ACCESS_KEY", "YOUR_SECRET_KEY", Amazon.Region.USWest2);\n\n// Upload an image\nvar request = new PutObjectRequest()\n{\n    BucketName = "your-bucket-name",\n    Key = "image.jpg",\n    InputStream = new FileStream("image.jpg", FileMode.Open),\n    ContentType = "image/jpeg"\n};\ns3Client.PutObject(request);