public class RobotsMetaTagFilter : ActionFilterAttribute\n{\n    public override void OnActionExecuted(ActionExecutedContext filterContext)\n    {\n        var queryString = filterContext.HttpContext.Request.QueryString;\n        if (queryString.Count > 0)\n        {\n            filterContext.Controller.ViewBag.RobotsMetaTag = "noindex";\n        }\n        else\n        {\n            filterContext.Controller.ViewBag.RobotsMetaTag = "all";\n        }\n    }\n}