public class DataAccessLayer\n{\n    public bool Method1()\n    {\n        using (var connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            using (var transaction = connection.BeginTransaction())\n            {\n                try\n                {\n                    // SQL commands here\n                    transaction.Commit();\n                    return true;\n                }\n                catch (Exception ex)\n                {\n                    transaction.Rollback();\n                    throw;\n                }\n            }\n        }\n    }\n\n    // Similarly for Method2, Method3, etc.\n}