// Example usage of PerformanceCounter to measure JIT compilation time\nusing System.Diagnostics;\n\n// Create a performance counter instance\nvar jitTimeCounter = new PerformanceCounter("JITCompilationTime");\n\n// Measure JIT compilation time for a specific method or operation\nvar startTime = DateTime.Now;\n// Your code here...\nvar endTime = DateTime.Now;\n\n// Get the JIT compilation time from the performance counter\nvar jitTime = jitTimeCounter.NextValue();\n\n// Calculate the elapsed time\nvar elapsedTime = (endTime - startTime).TotalMilliseconds;\n\n// Print or log the results\nDebug.WriteLine($"JIT compilation time: {jitTime} ms");\nDebug.WriteLine($"Elapsed time: {elapsedTime} ms");