using System.Text.RegularExpressions;\n\n// Hostname regex\nstring hostnameRegex = @"^[a-zA-Z0-9]([a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(\.[a-zA-Z0-9]([a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*$";\n\n// A record target regex\nstring aRecordTargetRegex = @"^(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$";\n\n// CNAME and MX record target regex (same as hostname)\nstring cnameMxRecordTargetRegex = hostnameRegex;\n\n// TXT record regex (printable ASCII characters)\nstring txtRecordRegex = @"^[\x20-\x7E]*$";\n\n// Example usage:\nstring userInputHostname = "example-hostname";\nif (!Regex.IsMatch(userInputHostname, hostnameRegex))\n{\n    // Handle invalid input\n}\n\nstring userInputARecordTarget = "192.0.2.1";\nif (!Regex.IsMatch(userInputARecordTarget, aRecordTargetRegex))\n{\n    // Handle invalid input\n}