using System.Xml.Linq;\n\n// Assuming you have an XElement or XDocument named 'xml'\n\nvar nodesIWantReturned = xml.Descendants("topNode")\n    .Elements("middleNode")\n    .Where(middleNode => (string)middleNode.Attribute("filteringAttribute") == "filterValue")\n    .Elements("penultimateNode")\n    .Where(penultimateNode => (string)penultimateNode.Attribute("anotherFilterAttribute") == "somethingElse")\n    .Elements("nodesIWantReturned");\n\n// nodesIWantReturned is now an IEnumerable<XElement> containing the desired nodes