using System;\nusing System.Net.Mail;\nusing System.Net;\n\npublic class EmailReader\n{\n    public string ReadEmailFromPopServer(string popServer, string username, string password)\n    {\n        // Create a new Pop3Client object\n        Pop3Client client = new Pop3Client();\n\n        // Connect to the POP server\n        client.Connect(popServer, 110, false);\n\n        // Authenticate the user\n        client.Authenticate(username, password);\n\n        // Get the number of messages on the server\n        int messageCount = client.GetMessageCount();\n\n        // Loop through each message\n        for (int i = 1; i <= messageCount; i++)\n        {\n            // Get the message\n            Message message = client.GetMessage(i);\n\n            // Check if the message has an XML attachment\n            if (message.Attachments.Count > 0)\n            {\n                // Get the first attachment\n                Attachment attachment = message.Attachments[0];\n\n                // Check if the attachment is an XML file\n                if (attachment.ContentType.MediaType == "text/xml")\n                {\n                    // Read the contents of the attachment\n                    string xmlContent = new StreamReader(attachment.ContentStream).ReadToEnd();\n\n                    // Process the XML content\n                    // ...\n\n                    // Return the XML content\n                    return xmlContent;\n                }\n            }\n        }\n\n        // Disconnect from the POP server\n        client.Disconnect();\n\n        // Return null if no XML attachment was found\n        return null;\n    }\n}