// Generate a JWT token with the user's credentials\nvar token = new JwtSecurityToken(\n    claims: new[] { new Claim(ClaimTypes.Name, "username") },\n    expires: DateTime.UtcNow.AddMinutes(30),\n    signingCredentials: new SigningCredentials(new SymmetricSecurityKey("your_secret_key"), SecurityAlgorithms.HmacSha256)\n);\n\n// Redirect to the new application with the JWT token\nResponse.Redirect($"https://newapp.com?token={token}");