using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing VaultSharp;\n\n// Initialize Vault client\nvar vaultClient = new VaultClient(new Uri("https://your-vault-instance.com"));\n\n// Authenticate with Vault using AppRole\nvar authResponse = await vaultClient.AuthenticateAsync(\n    new AppRoleAuthMethodInfo("your-app-role-id", "your-app-role-secret"));\n\n// Retrieve connection string from Vault\nvar connectionStringResponse = await vaultClient.Secrets.GetAsync(\n    "your-secret-path", "your-connection-string-key");\n\n// Use the connection string in your web application\nvar connectionString = connectionStringResponse.Data.Data.ToString();