public BuilderInclusionsForm(Product p) : this()\n{\n    Type foo = p.GetType();\n    MethodInfo getTableMethod = db2.GetType().GetMethod("GetTable", BindingFlags.Public | BindingFlags.Instance);\n    MethodInfo genericGetMethod = getTableMethod.MakeGenericMethod(foo);\n    object table = genericGetMethod.Invoke(db2, null);\n\n    MethodInfo singleOrDefaultMethod = typeof(Enumerable).GetMethod("SingleOrDefault", BindingFlags.Public | BindingFlags.Static);\n    MethodInfo genericSingleOrDefaultMethod = singleOrDefaultMethod.MakeGenericMethod(foo);\n    product = (Product)genericSingleOrDefaultMethod.Invoke(null, new object[] { table, a => a.ProductID == p.ProductID });\n}