<asp:UpdatePanel ID="UpdatePanel1" runat="server" OnUpdating="UpdatePanel1_Updating">\n    <ContentTemplate>\n        <!-- Your content here -->\n    </ContentTemplate>\n    <Triggers>\n        <asp:AsyncPostBackTrigger ControlID="RefreshButton" EventName="Click" />\n    </Triggers>\n</asp:UpdatePanel>\n\n<asp:Image ID="LoadingImage" runat="server" ImageUrl="~/loading.gif" Style="display: none;" />\n\nprotected void UpdatePanel1_Updating(object sender, EventArgs e)\n{\n    UpdatePanel1.ContentTemplateContainer.Style["display"] = "none";\n    LoadingImage.Style["display"] = "block";\n}\n\nprotected void UpdatePanel1_Updated(object sender, EventArgs e)\n{\n    UpdatePanel1.ContentTemplateContainer.Style["display"] = "block";\n    LoadingImage.Style["display"] = "none";\n}