using Castle.Windsor;\nusing Castle.MicroKernel.Registration;\n\n// Define the plugin interface\npublic interface IPlugin\n{\n    void DoSomething();\n}\n\n// Create a plugin implementation\npublic class MyPlugin : IPlugin\n{\n    public void DoSomething()\n    {\n        Console.WriteLine("MyPlugin is doing something");\n    }\n}\n\n// Create a Windsor container\nWindsorContainer container = new WindsorContainer();\n\n// Register the plugin interface and implementation\ncontainer.Register(\n    Component.For<IPlugin>().ImplementedBy<MyPlugin>()\n);\n\n// Resolve the plugin instance\nIPlugin plugin = container.Resolve<IPlugin>();\n\n// Use the plugin\nplugin.DoSomething();