using Tweetinvi;\n\n// Set up your Twitter API credentials\nvar auth = new TwitterCredentials("your_consumer_key", "your_consumer_secret", "your_access_token", "your_access_token_secret");\n\n// Create a new Twitter API client\nvar client = new TwitterClient(auth);\n\n// Retrieve the tweets from a specific user's timeline\nvar tweets = client.GetHomeTimeline();\n\n// Loop through the tweets and print the text\nforeach (var tweet in tweets)\n{\n    Console.WriteLine(tweet.Text);\n}