using System.Diagnostics;\n\npublic void CompactDatabase(string databasePath)\n{\n    string accessExecutable = @"C:\Program Files\Microsoft Office\root\Office16\MSACCESS.EXE";\n    string compactCommand = $"/compact {databasePath}";\n\n    using (Process process = new Process())\n    {\n        process.StartInfo.FileName = accessExecutable;\n        process.StartInfo.Arguments = compactCommand;\n        process.StartInfo.WindowStyle = ProcessWindowStyle.Hidden;\n        process.Start();\n        process.WaitForExit();\n    }\n}