using System.IO;\n\npublic class FolderCompressor\n{\n    public static void CompressFolder(string folderPath)\n    {\n        DirectoryInfo directoryInfo = new DirectoryInfo(folderPath);\n        if ((directoryInfo.Attributes & FileAttributes.Compressed) != FileAttributes.Compressed)\n        {\n            directoryInfo.Attributes |= FileAttributes.Compressed;\n        }\n    }\n}