using System.Data;\nusing System.Data.SqlClient;\n\n// Assume you have a SqlConnection named 'sqlConnection' and a DataTable named 'dataTable'\n\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n\n    using (SqlDataAdapter adapter = new SqlDataAdapter("SELECT * FROM YourTableName", connection))\n    {\n        adapter.Fill(dataTable);\n    }\n}