protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Assuming the textboxes are directly under the form\n        var form = Page.Form;\n\n        // Get the controls\n        var txt1 = form.FindControl("textbox1");\n        var txt2 = form.FindControl("textbox2");\n        var txt3 = form.FindControl("textbox3");\n\n        // Remove the controls from the form\n        form.Controls.Remove(txt1);\n        form.Controls.Remove(txt2);\n        form.Controls.Remove(txt3);\n\n        // Add them back in the desired order\n        form.Controls.Add(txt3);\n        form.Controls.Add(txt2);\n        form.Controls.Add(txt1);\n    }\n}