using System.Text.RegularExpressions;\n\nstring input = "O'Brian"; // with special apostrophe\n\n// Transliterate to ASCII\nbyte[] utf8Bytes = Encoding.UTF8.GetBytes(input);\nbyte[] asciiBytes = Encoding.Convert(Encoding.UTF8, Encoding.ASCII, utf8Bytes);\nstring asciiString = Encoding.ASCII.GetString(asciiBytes);\n\n// Remove special characters\nstring cleanString = Regex.Replace(asciiString, @"[^\x20-\x7E]", "");\n\nConsole.WriteLine(cleanString); // outputs "OBrian"