protected void gvMaster_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        LinkButton lbExpand = (LinkButton)e.Row.FindControl("lbExpand");\n        GridView gvDetail = (GridView)e.Row.FindControl("gvDetail");\n\n        lbExpand.Click += new EventHandler(lbExpand_Click);\n\n        gvDetail.DataSource = GetDetailData(e.Row.RowIndex);\n        gvDetail.DataBind();\n    }\n}\n\nprotected void lbExpand_Click(object sender, EventArgs e)\n{\n    LinkButton lbExpand = (LinkButton)sender;\n    GridViewRow row = (GridViewRow)lbExpand.NamingContainer;\n    GridView gvDetail = (GridView)row.FindControl("gvDetail");\n\n    if (gvDetail.Visible == false)\n    {\n        gvDetail.Visible = true;\n        lbExpand.Text = "-";\n    }\n    else\n    {\n        gvDetail.Visible = false;\n        lbExpand.Text = "+";\n    }\n}\n\nprivate DataTable GetDetailData(int masterRowIndex)\n{\n    // Get the detail data from the database based on the master row index\n    // ...\n    return detailData;\n}