using System;\nusing System.Diagnostics;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("kernel32")]\n    static extern IntPtr GetProcessHeap();\n\n    [DllImport("kernel32")]\n    static extern bool HeapWalk(IntPtr hHeap, ref PROCESS_HEAP_ENTRY lpEntry);\n\n    [StructLayout(LayoutKind.Sequential)]\n    public struct PROCESS_HEAP_ENTRY\n    {\n        public IntPtr lpData;\n        public uint cbData;\n        public uint cbOverhead;\n        public uint iRegionIndex;\n        public uint wFlags;\n    }\n\n    static void Main()\n    {\n        // Get the process heap\n        IntPtr hHeap = GetProcessHeap();\n\n        // Initialize the heap entry structure\n        PROCESS_HEAP_ENTRY entry = new PROCESS_HEAP_ENTRY();\n        entry.cbData = (uint)Marshal.SizeOf(entry);\n\n        // Walk the heap and print the size of each entry\n        while (HeapWalk(hHeap, ref entry))\n        {\n            Console.WriteLine($"Heap entry size: {entry.cbData} bytes");\n        }\n    }\n}