using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class RangeSearcher\n{\n    private readonly List<(int Start, int End)> _ranges;\n\n    public RangeSearcher(List<(int Start, int End)> ranges)\n    {\n        _ranges = ranges.OrderBy(r => r.Start).ToList();\n    }\n\n    public bool Contains(int value)\n    {\n        int index = _ranges.BinarySearch((value, value), Comparer);\n        if (index < 0) index = ~index;\n        if (index >= _ranges.Count) return false;\n        return value <= _ranges[index].End;\n    }\n\n    private static int Comparer((int Start, int End) x, (int Start, int End) y) => x.Start.CompareTo(y.Start);\n}