private static int FindPositionForArticle(string word, List<ArticleEntity> list)\n{\n    string key = word.ToLower();\n    int pos = 0;\n\n    while (key.Length > 0)\n    {\n        int insertPos = -1;\n        foreach (ArticleEntity article in list)\n        {\n            if (article.Text.ToLower().StartsWith(key))\n            {\n                insertPos = pos;\n                break;\n            }\n            pos++;\n        }\n        if (insertPos != -1)\n            return insertPos;\n        key = key.Substring(0, key.Length - 1);\n        pos = 0;\n    }\n    return list.Count; // insert at the end if no match found\n}