using iTextSharp.text.pdf;\n\n// Open the PDF file\nusing (var pdfReader = new PdfReader("path/to/your/pdf.pdf"))\n{\n    // Get the first page (change to the page you're interested in)\n    var page = pdfReader.GetPageN(1);\n\n    // Get the annotations\n    var annotations = page.GetAnnotations();\n\n    // Loop through annotations and extract text\n    foreach (var annotation in annotations)\n    {\n        var annotationText = annotation.GetContents();\n        // Do something with the annotation text\n    }\n}