using IdentityServer4;\n\n// Configure IdentityServer\nservices.AddIdentityServer()\n    .AddClients( clients =>\n    {\n        clients.AddClient("client1", client =>\n        {\n            client.RedirectUris = { "http://app1.com/callback" };\n            client.Secrets.Add(new Secret("secret1".Sha256()));\n        });\n        clients.AddClient("client2", client =>\n        {\n            client.RedirectUris = { "http://app2.com/callback" };\n            client.Secrets.Add(new Secret("secret2".Sha256()));\n        });\n    });\n\n// Use IdentityServer in your app\n[Authorize]\npublic class MyController : Controller\n{\n    public IActionResult Index()\n    {\n        // User is already authenticated\n        return View();\n    }\n}