// Create a new StreamReader to read the file\nusing (StreamReader reader = new StreamReader("C:\\Path\\To\\File.csv"))\n{\n    // Read the header row\n    string[] header = reader.ReadLine().Split(',');\n\n    // Read the data rows\n    while (!reader.EndOfStream)\n    {\n        // Read the next row\n        string[] row = reader.ReadLine().Split(',');\n\n        // Create a new Dictionary to store the values\n        Dictionary<string, string> values = new Dictionary<string, string>();\n\n        // Iterate over the header columns\n        for (int i = 0; i < header.Length; i++)\n        {\n            // Add the value to the Dictionary\n            values.Add(header[i], row[i]);\n        }\n\n        // Get the values by column name\n        string ourReference = values["OurReference"];\n        string client = values["Client"];\n        decimal amount = decimal.Parse(values["Amount"]);\n    }\n}