using Microsoft.SharePoint;\nusing Microsoft.SharePoint.WebPartPages;\nusing System.Web.UI.WebControls.WebParts;\n\nnamespace CustomHtmlEditorWebPart\n{\n    public class CustomHtmlEditorWebPart : WebPart\n    {\n        private AjaxControlToolkit.HTMLEditor.AjaxHtmlEditor _htmlEditor;\n\n        protected override void CreateChildControls()\n        {\n            base.CreateChildControls();\n\n            _htmlEditor = new AjaxControlToolkit.HTMLEditor.AjaxHtmlEditor();\n            _htmlEditor.TextChanged += new EventHandler(_htmlEditor_TextChanged);\n            this.Controls.Add(_htmlEditor);\n        }\n\n        void _htmlEditor_TextChanged(object sender, EventArgs e)\n        {\n            // Get the current page or item\n            SPWeb web = SPContext.Current.Web;\n            SPPage page = web.GetPage();\n\n            // Update the page or item content\n            page.Content = _htmlEditor.Content;\n            page.Update();\n        }\n    }\n}