using System.Net.Mail;\n\n// Create a MailAddressParser object\nMailAddressParser parser = new MailAddressParser();\n\n// Parse the email address\nMailAddress address = parser.Parse("myFirstName.O'Reilly@mywebsite.com");\n\n// Use the MailAddress object to send the email\nSmtpClient smtp = new SmtpClient();\nsmtp.Send("your_from_email@example.com", address.Address, "Subject", "Body");