using System.ServiceProcess;\nusing System.ServiceModel;\n\nnamespace AccessDatabaseWrapper\n{\n    public class AccessDatabaseService : ServiceBase\n    {\n        private ServiceHost host;\n\n        protected override void OnStart(string[] args)\n        {\n            host = new ServiceHost(typeof(AccessDatabase));\n            host.AddEndpoint(typeof(IAccessDatabase), new NetTcpBinding(), "net.tcp://localhost:8080/AccessDatabase");\n            host.Open();\n        }\n\n        protected override void OnStop()\n        {\n            host.Close();\n        }\n    }\n}