using System;\nusing WinPcap;\n\nclass Program\n{\n    static void Main()\n    {\n        // Initialize WinPcap\n        WinPcap.Init();\n\n        // Open the first network interface\n        IntPtr dev = WinPcap.OpenLive("\\Device\\NPF_{...}", 65535, 1, 0);\n\n        // Create a TCP packet\n        byte[] packet = new byte[54];\n        packet[0] = 0x45; // IP version and header length\n        packet[1] = 0x00; // TOS\n        packet[2] = (byte)((packet.Length - 14) >> 8); // Length\n        packet[3] = (byte)((packet.Length - 14) & 0xFF);\n        packet[12] = 0x08; // TCP protocol\n        packet[13] = 0x00; // Checksum\n        packet[14] = 0x12; // Source port\n        packet[15] = 0x34; // Destination port\n        packet[16] = 0x00; // Sequence number\n        packet[17] = 0x00; // Acknowledgment number\n        packet[18] = 0x50; // Header length and flags\n        packet[19] = 0x02; // Window size\n        packet[20] = 0x71; // Checksum\n        packet[21] = 0x00; // Urgent pointer\n\n        // Send the packet\n        WinPcap.SendPacket(dev, packet, packet.Length);\n\n        // Close the device\n        WinPcap.Close(dev);\n\n        // Uninitialize WinPcap\n        WinPcap.Uninit();\n    }\n}