using Telerik.OpenAccess;\n\n// Define a data model class\npublic class Customer\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\n// Create a data context\npublic class MyDbContext : OpenAccessContext\n{\n    public MyDbContext(string connectionString) : base(connectionString)\n    {\n    }\n\n    public IQueryable<Customer> Customers\n    {\n        get { return GetAll<Customer>(); }\n    }\n}\n\n// Use the data context to retrieve data\nMyDbContext dbContext = new MyDbContext("my_connection_string");\nIQueryable<Customer> customers = dbContext.Customers;\nforeach (Customer customer in customers)\n{\n    Console.WriteLine(customer.Name);\n}