using iTextSharp.text;\nusing iTextSharp.text.pdf;\nusing iTextSharp.tool.xml;\n\n// Create a new PDF document\nDocument doc = new Document();\nPdfWriter writer = PdfWriter.GetInstance(doc, new FileStream("output.pdf", FileMode.Create));\n\n// Set the HTML content\nstring htmlContent = "<html><body>Hello World!</body></html>";\n\n// Convert the HTML to PDF\nXMLWorkerHelper.GetInstance().ParseXHtml(writer, doc, new StringReader(htmlContent));\n\n// Close the document\ndoc.Close();