using System;\nusing System.IO;\nusing System.Security.Cryptography;\n\nclass Program\n{\n    static void Main()\n    {\n        // Initialize AES-CTR\n        var aes = Aes.Create();\n        aes.KeySize = 256;\n        aes.BlockSize = 128;\n        aes.Mode = CipherMode.CTR;\n        aes.Padding = PaddingMode.None;\n\n        // Encrypt data\n        var plaintext = new byte[1000];\n        var ciphertext = new byte[1000];\n        using (var encryptor = aes.CreateEncryptor())\n        {\n            encryptor.TransformBlock(plaintext, 0, plaintext.Length, ciphertext, 0);\n        }\n\n        // Decrypt data starting from offset 1 with length 500\n        var decrypted = new byte[500];\n        using (var decryptor = aes.CreateDecryptor())\n        {\n            decryptor.TransformBlock(ciphertext, 1, 500, decrypted, 0);\n        }\n    }\n}