// Sample dictionary\nvar fieldConfig = new Dictionary<string, List<string>>\n{\n    {"Movie", new List<string> {"Title", "Genre", "ReleaseDate"}},\n    {"Game", new List<string> {"Title", "Platform", "Genre"}},\n    // ...\n};\n\n// In Add.aspx.cs\nprotected void Page_Load(object sender, EventArgs e)\n{\n    string objectType = Request.QueryString["type"]; // or use a dropdown to select the type\n    if (!string.IsNullOrEmpty(objectType))\n    {\n        var fields = fieldConfig[objectType];\n        foreach (var field in fields)\n        {\n            // Create a form control for each field\n            var label = new Label { Text = field };\n            var textBox = new TextBox { ID = field };\n            // Add the controls to the page\n            Form.Controls.Add(label);\n            Form.Controls.Add(textBox);\n        }\n    }\n}