public class YourDataContext : DataContext\n{\n    public Table<Company> Companies;\n    public Table<Contact> Contacts;\n\n    public YourDataContext() : base("YourConnectionString") { }\n}\n\n[Table(Name = "Companies")]\npublic class Company\n{\n    [Column(IsPrimaryKey = true)]\n    public int Id { get; set; }\n\n    [Column]\n    public string Company { get; set; }\n\n    [Association(ThisKey = "Id", OtherKey = "CompanyId")]\n    public EntitySet<Contact> Contacts { get; set; }\n}\n\n[Table(Name = "Contacts")]\npublic class Contact\n{\n    [Column(IsPrimaryKey = true)]\n    public int Id { get; set; }\n\n    [Column]\n    public int CompanyId { get; set; }\n\n    [Column]\n    public string Contact { get; set; }\n}