using System;\nusing System.Data.SqlClient;\n\npublic class ForeignKeyConstraints\n{\n    public static void GetForeignKeyConstraints(string connectionString, string tableName)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            DataTable foreignKeysTable = connection.GetSchema("ForeignKeys", new string[] { tableName });\n\n            foreach (DataRow row in foreignKeysTable.Rows)\n            {\n                string foreignKeyName = row["CONSTRAINT_NAME"].ToString();\n                string relatedTable = row["TABLE_NAME"].ToString();\n                string relatedColumn = row["COLUMN_NAME"].ToString();\n\n                Console.WriteLine($"** FK ** {foreignKeyName} - RelatedTable: {relatedTable}; RelatedColumn: {relatedColumn}");\n            }\n        }\n    }\n}