using System;\nusing System.IO;\nusing System.Windows.Forms;\nusing iTextSharp.text.pdf;\nusing iTextSharp.text.pdf.parser;\nusing HtmlAgilityPack;\n\npublic class FileContentsReader\n{\n    public string ReadRtfFile(string filePath)\n    {\n        var richTextBox = new RichTextBox();\n        richTextBox.LoadFile(filePath);\n        return richTextBox.Text;\n    }\n\n    public string ReadPdfFile(string filePath)\n    {\n        var pdfReader = new PdfReader(filePath);\n        var text = PdfTextExtractor.GetTextFromPage(pdfReader, 1);\n        pdfReader.Close();\n        return text;\n    }\n\n    public string ReadHtmlFile(string filePath)\n    {\n        var htmlDocument = new HtmlDocument();\n        htmlDocument.Load(filePath);\n        return htmlDocument.DocumentNode.InnerText;\n    }\n\n    public string ReadWordFile(string filePath)\n    {\n        var wordApplication = new Microsoft.Office.Interop.Word.Application();\n        var wordDocument = wordApplication.Documents.Open(filePath);\n        var text = wordDocument.Content.Text;\n        wordDocument.Close();\n        wordApplication.Quit();\n        return text;\n    }\n}