public partial class frmPin : Form\n{\n    static string connString = @"Provider=Microsoft.ACE.OLEDB.12.0;Data Source=C:\Users\Public;Mode=Share Deny None;Persist Security Info=True";\n\n    public frmPin()\n    {\n        InitializeComponent();\n    }\n\n    private void btnQry_Click(object sender, EventArgs e)\n    {\n        try\n        {\n            using (OleDbConnection conn = new OleDbConnection(connString))\n            {\n                conn.Open();\n\n                string selectStatement = "SELECT Amount FROM Accounts WHERE PIN = @pin";\n                OleDbCommand cmd = new OleDbCommand(selectStatement, conn);\n                cmd.Parameters.AddWithValue("@pin", 11); // replace with the actual pin value\n\n                using (OleDbDataReader reader = cmd.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        txtBx.Text = reader.GetValue(0).ToString();\n                    }\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            txtBx.Text = "Exception: " + ex;\n        }\n    }\n}