using System;\nusing System.IO;\nusing System.Reflection;\n\nclass SignAssembly\n{\n    static void Main(string[] args)\n    {\n        // Load the assembly\n        Assembly assembly = Assembly.LoadFrom("MyAssembly.dll");\n\n        // Load the key pair\n        byte[] keyPair = File.ReadAllBytes("MyKeyPair.snk");\n\n        // Sign the assembly\n        assembly.GetName().SetPublicKey(keyPair);\n        assembly.GetName().SetPublicKeyToken(keyPair);\n    }\n}